function _calcCRC16 (buf, size) {
		var i = 0 ;
		var seed = 0x3692 ;
		while (size > 0) {
			seed = TBL_CRC16[(seed ^ buf[i]) & 0xff] ^ (seed >> 8)
			i++ ;
			size-- ;
		} 
		return seed ;
	}

function _calcCRC8 (buf, size) {
		var i = 0 ;
		var seed = 0x77 ;
		while (size > 0) {
			seed =  TBL_CRC8[(seed ^ buf[i]) & 0xff]
			i++ ;
			size-- ;
		}
		return seed ;
	}


function buildPacket(pacType, cmdID, seq, data) {
		var size = 11 + (data?data.length:0);
		var bb = Buffer.alloc(size)
		var packetTypeInfo = 0x40 | ((pacType & 0x07) <<3);
		bb.writeUInt8(0xCC,0)
		bb.writeUInt8((size << 3) & 0xFF ,1)
		bb.writeUInt8((size >> 5) & 0xFF ,2)

    	var crc8 = _calcCRC8([...bb], 3)
		bb.writeUInt8(crc8,3);
		bb.writeUInt8(packetTypeInfo,4);
		bb.writeUInt16LE(cmdID,5);
		bb.writeUInt16LE(seq,7);
		//console.log("DBG", data, len);
		if (data) {
			for (var i=0; i<data.length; i++) {
				bb.writeUInt8(data[i], [9+i]);
			}
		}
		var crc16 = _calcCRC16([...bb], size - 2);
		bb.writeUInt16LE(crc16, size-2) ;
		return bb ;
	}

