function parsePacket (bb) {
    var ret = {};
	var size = bb.length;
	var payload = bb.slice(9,size);
	var dataSize = 0;
	var cmdID    = 0;

	if (bb.length >= 11) {
		var mark = bb.readUInt8(0);
		if (mark == 0xCC) {	// start of packet
		    size      = bb.readUInt16LE(1) >> 3;
		    var crc8  = bb.readUInt8(3);
		    pacType   = bb.readUInt8(4);
		    cmdID     = bb.readUInt16LE(5);
		    seqID     = bb.readUInt16LE(7);
	        dataSize  = size - 11;
	        var data  = false;
	        if (dataSize > 0) {
	            data = bb.slice(9, size-2);
	        }
	        var crc16 = bb.readUInt16LE(size - 2);

	        ret.cmd = cmdID;
	        ret.seq = seqID;
	        ret.pacType= pacType;
	        ret.dataSize = dataSize;
	        ret.data = data;
		} else {
		    ret = bb.toString();
		}
	} else {
	    ret = bb.toString();
	}
	return ret;
}

