    } else {
        msg.payload = {};
        msg.payload.unpackedHeader = {"ID" : fileID, 
                "piece" : filePiece,
                "chunk" : fileChunk,
                "len" : chunkLen
        };
        file.pieces[filePiece] = [fileChunk];
        file.receivedBytes += chunkLen;
    }
    msg.payload.unpackedHeader.receivedBytes = file.receivedBytes;

    if (file.receivedBytes == file.size) {
        flow.set (["file" + fileID], null);
        msg.payload.TelloCmdId = 99;
        msg.payload.packType = 2;
        msg.payload.data = Buffer.alloc(7);
        msg.payload.data.writeInt8(1, 0);
        msg.payload.data.writeInt16LE(fileID, 1);
        msg.payload.data.writeInt32LE(filePiece, 3);

        msg1 = {"payload" : {}};
        msg1.payload.TelloCmdId = 100;
        msg1.payload.packType = 1;
        return [[msg, msg1], {"payload":file.imageBytes}];
    } else if (msg.payload.TelloCmdId) {
        flow.set("file" + fileID, file);
        return [msg, null];
    } else {
        flow.set("file" + fileID, file);
        return [null, null];
    }
}

